	// Converter InputStream para array de bytes (Para os arquivos)
	public static byte[] getInputStreamToBytes(InputStream is) throws IOException {
		@SuppressWarnings("unused")
		int len;
		int size = 1024;
		byte[] buf = null;

		if (is instanceof ByteArrayInputStream) {
			size = is.available();
			buf = new byte[size];
			len = is.read(buf, 0, size);
		} else {
			ByteArrayOutputStream bos = new ByteArrayOutputStream();
			buf = new byte[size];
			while ((len = is.read(buf, 0, size)) != -1) {
				bos.write(buf, 0, size);
			}

			buf = bos.toByteArray();
		}

		return buf;
	}
	
	public static boolean isEmpty(String s) {
		return s == null || s.isEmpty();
	}

	public static String getImagemBase64(Part imagem) throws IOException {
		BufferedImage bi = ImageIO.read(new ByteArrayInputStream(getInputStreamToBytes(imagem.getInputStream())));

		ByteArrayOutputStream baos = new ByteArrayOutputStream();
		ImageIO.write(bi, imagem.getContentType().split("/")[1], baos);
		Base64.Encoder encoder = Base64.getEncoder();
		String imagemGrande = "data:" + imagem.getContentType() + ";base64,"
				+ encoder.encodeToString(baos.toByteArray());

		return imagemGrande;
	}

	public static String getImagemBase64(byte[] imagem) throws IOException {
		BufferedImage bi = ImageIO.read(new ByteArrayInputStream(imagem));

		ByteArrayOutputStream baos = new ByteArrayOutputStream();
		ImageIO.write(bi, "png", baos);
		Base64.Encoder encoder = Base64.getEncoder();
		String imagemGrande = "data:image/png" + ";base64," + encoder.encodeToString(baos.toByteArray());

		return imagemGrande != null && !imagemGrande.isEmpty() ? imagemGrande : null;
	}

	public static String getImagemBase64Miniatura(Part imagem) throws IOException {
		BufferedImage bi = ImageIO.read(new ByteArrayInputStream(getInputStreamToBytes(imagem.getInputStream())));

		int type = bi.getType() == 0 ? BufferedImage.TYPE_INT_ARGB : bi.getType();

		int largura = bi.getWidth() / 5;
		int altura = bi.getHeight() / 5;

		BufferedImage tamanhoMenor = new BufferedImage(largura, altura, type);

		Graphics2D graphics = tamanhoMenor.createGraphics();
		graphics.drawImage(bi, 0, 0, largura, altura, null);
		graphics.dispose();

		ByteArrayOutputStream baos = new ByteArrayOutputStream();
		ImageIO.write(tamanhoMenor, imagem.getContentType().split("/")[1], baos);
		Base64.Encoder encoder = Base64.getEncoder();
		String miniatura = "data:" + imagem.getContentType() + ";base64," + encoder.encodeToString(baos.toByteArray());

		return miniatura;
	}

	public static String postRequestBearer(String url, String content, boolean isAuth, String token, String httpMethod,
			Integer timeout) throws Exception {
		HttpURLConnection connection = null;
		OutputStream os = null;

		try {
			URL postUrl = new URL(url);
			connection = (HttpURLConnection) postUrl.openConnection();
			connection.setDoOutput(true);
			connection.setDoInput(true);

			connection.setRequestMethod(httpMethod);
			connection.setRequestProperty("Content-Type", "application/json");
			if (isAuth) {
				connection.setRequestProperty("Authorization", "Bearer " + token);
			}

			if (timeout != null) {
				connection.setConnectTimeout(timeout);
			}

			os = connection.getOutputStream();
			if (content != null && !content.isEmpty())
				os.write(content.getBytes("UTF-8"));
			os.flush();

			boolean error = false;
			InputStream is;

			/**
			 * Se o codigo e de http 200 (OK).
			 */
			if (connection.getResponseCode() == 200 || connection.getResponseCode() == 201) {
				is = connection.getInputStream();
			} else {
				error = true;
				is = connection.getErrorStream();
			}

			BufferedReader reader = new BufferedReader(new InputStreamReader(is, "UTF-8"));

			StringBuilder sbResp = new StringBuilder();

			String line = reader.readLine();
			while (line != null) {
				sbResp.append(line);
				line = reader.readLine();
			}

			if (error) {
				Gson gson = new GsonBuilder().setPrettyPrinting().serializeNulls().create();
				try {
					if (sbResp != null && !sbResp.toString().isEmpty()) {

						LoginException response = gson.fromJson(sbResp.toString(), LoginException.class);
						if (response.getMessage() != null && !response.getMessage().isEmpty()) {
							String[] message = response.getMessage().split("#");

							if (message[0] != null) {
								if (Integer.valueOf(message[0]) == 99) {
									throw new LoginException(message[1]);
								}
							}
						}
					}
				} catch (Exception e) {

				}

				throw new Exception(sbResp.toString());
			}

			return sbResp.toString();

		} finally {
			if (os != null) {
				try {
					os.close();
				} catch (Exception e) {
				}
			}

			if (connection != null) {
				try {
					connection.disconnect();
				} catch (Exception e) {
				}
			}
		}
	}

	public static String removeAcentuacaoEEspaco(String value) {
		String normalizer = Normalizer.normalize(value, Normalizer.Form.NFD);
		Pattern pattern = Pattern.compile("[^\\p{ASCII}]");
		return pattern.matcher(normalizer).replaceAll("").replaceAll("\\p{Punct}", "").replace(" ", "").trim().toLowerCase();
	}
	
	public static void main(String[] args) {
		System.out.println(removeAcentuacaoEEspaco("txto:?perg unta?12ó  Wòeód"));
	}

	public static String removeAcentuacao(String value) {
		String normalizer = Normalizer.normalize(value, Normalizer.Form.NFD);
		Pattern pattern = Pattern.compile("[^\\p{ASCII}]");
		return pattern.matcher(normalizer).replaceAll("");
	}

	public static Integer gerarHash(String string1, String string2, Integer hashcode) {
		Integer hashs = string1.hashCode() + string2.hashCode() + hashcode;
		return hashs > 0 ? hashs : hashs * -1;
	}

	public static String operadorTernario(String string) {
		return string != null && !string.isEmpty() ? string : null;
	}

	public static void mostrarMsg(String string) {
		FacesContext context = FacesContext.getCurrentInstance();

		context.getExternalContext().getFlash().setKeepMessages(true);

		context.addMessage(null, new FacesMessage(string));
	}

	public static HttpSession sessao() {
		HttpServletRequest req = (HttpServletRequest) FacesContext.getCurrentInstance().getExternalContext()
				.getRequest();
		return req.getSession();
	}

	public static void updateAjax(String id) {
		FacesContext.getCurrentInstance().getPartialViewContext().getRenderIds().add(id);
	}

	public static void inserirObjetoNaSessao(String chave, Object objeto) {
		if (sessao().getAttribute(chave) != null) {
			sessao().removeAttribute(chave);
			System.out.println("Sessao limpa");
		}

		sessao().setAttribute(chave, objeto);
		System.out.println("Sessao atribuida!");
	}

	public static Object buscarObjetoNaSessao(String chave) {
		return sessao().getAttribute(chave);
	}

	public static void removerObjetoDaSessao(String chave) {
		sessao().removeAttribute(chave);
	}

	public static String readUrl(String urlString) throws Exception {
		URL url = new URL(urlString);
		URLConnection connection = url.openConnection();
		InputStream is = connection.getInputStream();
		BufferedReader br = new BufferedReader(new InputStreamReader(is, "UTF-8"));

		String string = "";
		StringBuilder json = new StringBuilder();

		while ((string = br.readLine()) != null) {
			json.append(string);
		}

		return json.toString();
	}

	public static void executeJavaScript(String js) {
		FacesContext.getCurrentInstance().getPartialViewContext().getEvalScripts().add(js);
	}

	public static String converterMoedaDouble(Double moeda) {
		Locale locale = new Locale("pt", "BR");
		return NumberFormat.getCurrencyInstance(locale).format(moeda);
	}

	public static void redirecionarPagina(String pagina) throws IOException {
		FacesContext.getCurrentInstance().getExternalContext().redirect(pagina);
	}

	public static boolean validaBotaoSalvar(Part im, String ti, Double pr, Long ca, String co, String ci) {
		if (im != null && (ti != null && !ti.isEmpty()) && pr != null && ca != null && (co != null && !co.isEmpty())
				&& (ci != null && !ci.isEmpty())) {
			return false;
		} else {
			return true;
		}
	}
	
	public static String gerarToken(String s1, String s2, String s3, Object o1) {
		Integer hashs = s1.hashCode() + s2.hashCode() + s3.hashCode() +  o1.hashCode();
		hashs = hashs > 0 ? hashs : (hashs * -1);
		return hashs.toString(); 
	}
